/* Get Service From Dashboard API */
// ---> Circumvent Hotlinking
const proxyUrl = "https://corsproxy.io/?";
const baseURL = "http://saloni-beauty.kesug.com";
const apiUrl = `${baseURL}/api/services`; // Replace with your actual API URL

// Fetch the services data
async function fetchServices() {
  try {
    const response = await fetch(proxyUrl + apiUrl)
    .then((response) => response.json())
    .then((data) => displayServices(data))
    .catch((error) => console.error("Error fetching services:", error));

    if (!response.ok) {
      throw new Error("Error fetching services");
    }
    const data = await response.json();
    displayServices(data);
  } catch (error) {
    console.error("Error fetching services:", error);
    // Handle the error here, e.g., display an error message to the user
  }
}

function displayServices(services) {
  const servicesContainer = document.getElementById("services-container");
  servicesContainer.innerHTML = "";

  services.forEach((service) => {
    const serviceCard = document.createElement("div");
    serviceCard.className = "card my-1 mx-2";
    serviceCard.style = "width: 50%; margin: 10px";

    const serviceElement = document.createElement("div");
    serviceElement.className = "service flex justify-content-between";

    // Create image element
    const imgElement = document.createElement("img");
    imgElement.className = "card-img-top";
    imgElement.alt = service.title;

    // Fetch the image without Referer header
    fetch(proxyUrl + `${baseURL}/${service.photo}`, {
      method: "GET",
      mode: "cors",
      referrerPolicy: "no-referrer", // سياسة عدم إرسال الرأس Referer
    })
      .then((response) => {
        if (response.ok) {
          return response.blob();
        }
        throw new Error("Failed to load image");
      })
      .then((blob) => {
        imgElement.src = URL.createObjectURL(blob);
      })
      .catch((error) => {
        console.error("Error loading image:", error);
        imgElement.alt = "Failed to load image";
      });

    const service_item2 = document.createElement("div");
    service_item2.className = "flex justify-content-between";
    service_item2.style = "margin-top: 15px;";

    const whatsappButton = document.createElement("div");
    whatsappButton.className = "pb-2 px-2 flex flex-column";
    whatsappButton.innerHTML = `
            <a class="btn service-btn"
          href="https://api.whatsapp.com/send?phone=966542988939&text=مرحبا اريد حجز موعد لـ ${service.title}"
          target="_blank">إحجز موعد</a>
    `;

    service_item2.innerHTML = `
            <div class="card-body">
          <h5 class="card-title">${service.title}</h5>
          <p class="card-text">${service.description}</p>
        </div>
        <hr />
        <div class="pb-2 px-2 flex flex-column flex-direction-column">
          <div class="flex align-end flex-direction-column">
            <div class="mx-2"><b>السعر: </b> ${service.price} ﷼</div>
            <div class="mx-2"><b>عرض: </b> ${service.offer} ﷼</div>
          </div>
        </div>
    `;

    serviceCard.appendChild(imgElement);
    serviceCard.appendChild(service_item2);
    serviceCard.appendChild(whatsappButton);

    // Append service card to container
    servicesContainer.appendChild(serviceCard);
  });
}

window.onload = () => {
  fetchServices();
}


/* Remove OR Show Home Page */
if (window.location.pathname !== '/') {
  const home = document.querySelector('#home-content')
  home.remove()
} else {
  const home = document.querySelector('#home-content')
  const bocker = document.querySelector('.bocker');
  home.style.display = 'block';
  bocker.remove();
}


